    public function saveSchedule($day, $start, $end, $docIds)
    {
        $schedule = Schedule::create([
            'hospital_id' => 1, 
            'day' => $day,
            'start_at' => $start,
            'end_at' => $end
        ]);
        foreach($docIds as $key=> $docId) {
            DoctorSchedule::create([
                'doctor_id' => $docId,
                'schedule_id' => $schedule->id
            ]);
            ++$key;
        }
    }
